mapper.update:
UPDATE
	TB_BUY_MST
SET
	CD_STATE_PAY	= #{cd_state_pay}
	, DT_UPT		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	SEQ_BUY_MST 	= #{seq_buy_mst}

mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY DT_REG DESC) rnum , SEQ_BUY_MST , SEQ_CST , BUY_INFO , BUY_T_PRICE , DECODE(CD_STATE_PAY, 'N', '결제전', 'Y', '결제완료', 'C', '결제취소', '기타') CD_STATE_PAY , DECODE(CD_STATE_DELIVERY, 'N', '배송전', 'Y', '배송완료', 'D', '배송중', '기타') CD_STATE_DELIVERY , TO_CHAR(DT_REG, 'YYYY-MM-DD') DT_REG , REQUEST FROM TB_BUY_MST WHERE SEQ_CST = #{register} AND
CD_STATE_PAY <> 'N'
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum

mapper.update:
UPDATE
	TB_BUY_MST
SET
	CD_STATE_PAY	= #{cd_state_pay}
	, DT_UPT		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	SEQ_BUY_MST 	= #{seq_buy_mst}
