mapper.select:
SELECT ROWNUM rnum , TB_SLE.SEQ_SLE , SLE_NM , CD_CTG , IMG , DESCES , PRICE_SALE , NVL(avg(rating), 0) rating FROM TB_SLE LEFT JOIN TB_REV ON TB_SLE.SEQ_SLE = TB_REV.SEQ_SLE WHERE CD_STATE_SALE = 1 AND CD_CTG LIKE ('%' || #{cd_ctg} || '%')
AND rownum <= #{currentPage}
GROUP BY TB_SLE.SEQ_SLE , TB_SLE.SLE_NM , TB_SLE.CD_CTG , TB_SLE.IMG , TB_SLE.DESCES , TB_SLE.PRICE_SALE , ROWNUM ORDER BY rating DESC

SELECT ROWNUM as RNUM , TB_SLE.SEQ_SLE , SLE_NM , TB_SLE.CD_CTG , IMG , DESCES , PRICE_SALE , TB_CTG.CTG_NM , NVL(avg(rating), 0) rating FROM TB_SLE LEFT JOIN TB_REV ON TB_SLE.SEQ_SLE = TB_REV.SEQ_SLE INNER JOIN TB_CTG ON TB_SLE.CD_CTG = TB_CTG.CD_CTG , TB_CTG TB_CTG2 WHERE CD_STATE_SALE = 1 AND TB_CTG.CD_CTG_PARENT = TB_CTG2.CD_CTG AND (SLE_NM LIKE ('%' || #{searchWord} || '%') OR TB_CTG.CTG_NM LIKE ('%' || #{searchWord} || '%') OR TB_CTG2.CTG_NM LIKE ('%' || #{searchWord} || '%'))
AND rownum <= #{currentPage}
GROUP BY TB_SLE.SEQ_SLE , TB_SLE.SLE_NM , TB_SLE.CD_CTG , TB_SLE.IMG , TB_SLE.DESCES , TB_SLE.PRICE_SALE , TB_CTG.CTG_NM , ROWNUM ORDER BY rating DESC
